            </div> */}



<div className="container-fluied text-center" style={{ marginTop: "20px", padding: "50px" }}>
        <div className="row">

            <input icon='search'
                placeholder='Search...'
                onChange={(e) => searchItems(e.target.value)}
            />
            <div itemsPerRow={3} style={{ marginTop: 20 }}>
                {searchInput.length > 1 ? (
                    filteredResults.map((item , index) => {
                        return (
                            <div className="col" key={index}>
                                <Card style={{ width: '18rem', marginTop: '20px' }}>
                                    <Card.Header>{item.title} </Card.Header>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>{item.year}</ListGroup.Item>
                                        <ListGroup.Item>{item.budget}</ListGroup.Item>
                                        <ListGroup.Item>
                                            <Link className="btn btn-danger" to={`/editmovie/${item.id}/`}>Edit</Link>
                                            <button className="btn btn-success" onClick={() => deleteMovie(item.id)} >Delete</button>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Card>

                            </div>
                        )
                    })
                ) : (
                    movies.map((item, index) => {
                        return (
                            <div className="col" key={index}>
                                <Card style={{ width: '18rem', marginTop: '20px' }}>
                                    <Card.Header>{item.title} </Card.Header>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>{item.year}</ListGroup.Item>
                                        <ListGroup.Item>{item.budget}</ListGroup.Item>
                                        <ListGroup.Item>
                                            <Link className="btn btn-danger" to={`/editmovie/${item.id}/`}>Edit</Link>
                                            <button className="btn btn-success" onClick={() => deleteMovie(item.id)} >Delete</button>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Card>

                            </div>
                        )
                    })
                )}
            </div>
        </div>
</div>





        </>
    );


}

