import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect  } from "react";
import { Navbar } from "react-bootstrap";
import Card from 'react-bootstrap/Card';
import { Link } from "react-router-dom";



const MovieList = () => {


    let [movies, setmovies] = useState([]);
    useEffect(() => {

        let movies = JSON.parse(localStorage.getItem('movies'));
        if (movies) {
            setmovies(movies);
        }

        console.log(movies)
    }, []);



return (
    <>
       <div className="container-fluid text-center" style={{marginTop:"20px"  ,padding:"50px"}}>
                <div className="row">
                {movies.map((movie) =>{

                    return(
                            <div className="col-lg-2">
                                <Card className=" mb-3" style={{borderStyle:"none"}}>

                                 {/* <h2>{{movie.moviename}}</h2> */}
                                       <Link className="text-warning" id="fav_btn" style={{marginRight:"auto",display:"block" ,position:"relative",fontSize:"40px" ,top:"-65px" ,left:"10px"}}><i className={"far fa-star"}></i></Link>
                                       <h3 style={{textDecoration:"none" ,color:"black" ,display:"block" ,position:"relative" ,top:"-40px"}}>{movie.title}</h3>
                                </Card>

                            </div>  
                    )
                }) 
                }
                </div> 
            </div>
        </>
); 



}

export default MovieList;
