import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import '../../css/style.css'
import axios from "axios";
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';
import React, { Component } from 'react';


const MovieList2 = () => {





    const [movies, setmovies] = useState([]);
    const [filteredResults, setFilteredResults] = useState([]);
    const [searchInput, setSearchInput] = useState('');

    const getMovies = async () => {
        try {
            const response = await axios.get('/movies')
            const { data } = response
            console.log(data)
            setmovies(data)
        } catch (err) {
            console.log(err)
        }
    }


    useEffect(() => {
        getMovies()
    }, []);


    const deleteMovie = async (id) => {

        try {
            await axios.delete(`/movies/${id}/`).then((response) => {
                getMovies()
                console.log(response.data)
            })

        } catch (error) {
            console.log(error)
        }

    }


    const searchItems = (searchValue) => {
        setSearchInput(searchValue)
        if (searchInput !== '') {
            const filteredData = movies.filter((item) => {
                return Object.values(item).join('').toLowerCase().includes(searchInput.toLowerCase())
            })
            setFilteredResults(filteredData)
        }
        else{
            setFilteredResults(movies)
        }
    }


    return (
        <>
            {/* <div className="container text-center" style={{ marginTop: "20px", padding: "50px" }}>
                <div className="row">
                    {movies.map((movie, index) => {

                        return (
                            <div className="col" key={index}>
                                <Card style={{ width: '18rem', marginTop: '20px' }}>
                                    <Card.Header>{movie.title} </Card.Header>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>{movie.year}</ListGroup.Item>
                                        <ListGroup.Item>{movie.budget}</ListGroup.Item>
                                        <ListGroup.Item>
                                            <Link className="btn btn-danger" to={`/editmovie/${movie.id}/`}>Edit</Link>
                                            <button className="btn btn-success" onClick={() => deleteMovie(movie.id)} >Delete</button>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Card>

                            </div>
                        )
                    })
                    }
                </div>
            </div> */}



 <div style={{ padding: 20 }}>
            <Input icon='search'
                placeholder='Search...'
                onChange={(e) => searchItems(e.target.value)}
            />
            <Card.Group itemsPerRow={3} style={{ marginTop: 20 }}>
                {searchInput.length > 1 ? (
                    filteredResults.map((item) => {
                        return (
                            <Card>
                                <Card.Content>
                                    <Card.Header>{item.name}</Card.Header>
                                    <Card.Description>
                                        {item.email}
                                    </Card.Description>
                                </Card.Content>
                            </Card>
                        )
                    })
                ) : (
                    movies.map((item) => {
                        return (
                            <Card>
                                <Card.Content>
                                    <Card.Header>{item.name}</Card.Header>
                                    <Card.Description>
                                        {item.email}
                                    </Card.Description>
                                </Card.Content>
                            </Card>
                        )
                    })
                )}
            </Card.Group>
        </div>






        </>
    );


}

export default MovieList2;
