            try {
                 axios.post('/movies', {
                     "title": addFormData.moviename,
                     "year": parseInt(addFormData.year),
                     "budget": parseInt(addFormData.moviebudjet),
                     "category_ids": [parseInt(addFormData.category)],
                 }).then((response) => {
                    setErrorMessage('')
                    err.style.visibility = "hidden"
                    console.log(response.data)
                })

            } catch (error) {
                console.log(error)
            }
        }




    }





    return (
        <>

            <div className="container" style={{ marginTop: "50px" }} >

                <div align="center" className="col-12 text-center" id="error" >
                    <span>{errorMessage}</span>
                    <br />
                </div>

                <form onSubmit={saveIntoApi}>
                    <div className="form-group">
                        <label htmlFor="moviename">Movie Name</label>
                        <input type="text" className="form-control" id="moviename" name="moviename" onChange={handleAddFormChange} />
                    </div>
                    <div className="input-group mb-3">
                        <div className="input-group-prepend">
                            <label className="input-group-text" htmlFor="year">Movie Year: </label>
                        </div>
                        <select className="custom-select" id="year" name="year" onChange={handleAddFormChange}   >

                        </select>
                    </div>
                    <div className="input-group mb-3">
                        <div className="input-group-prepend">
                            <label className="input-group-text" htmlFor="category">Movie Category: </label>
                        </div>
                        <select className="custom-select" id="category" name="category" onChange={handleAddFormChange}  >

                            <option selected>category</option>
                            <option value="0">Action</option>
                            <option value="1">Romantic</option>
                            <option value="3">Cartoon</option>
                            <option value="3">Horror</option>


                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="moviename">Movie Budget</label>
                        <input type="text" className="form-control" id="moviebudjet" name="moviebudjet" onChange={handleAddFormChange} />
                    </div>

                    <button type="submit" className="btn btn-primary" style={{ marginTop: "5px" }}>Submit</button>
                    <a className="btn btn-primary" style={{ marginTop: "5px" }} href="movielist">movielist</a>
                </form>

            </div>

        </>
    );



}

