import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect } from "react";
// import { Link } from "react-router-dom";
import '../../css/style.css'
import axios from "axios";
import ListGroup from 'react-bootstrap/ListGroup';
import Card from 'react-bootstrap/Card';


const MovieList2 = () => {





    const [movies, setmovies] = useState([]);

    const getMovies = async () => {
        try {
            const response = await axios.get('/movies')
            const { data } = response
            console.log(data)
            setmovies(data)
        } catch (err) {
            console.log(err)
        }
    }


    useEffect(() => {
        getMovies()
    }, []);


    const [errorMessage, setErrorMessage] = useState('');


    const [editFormData, setEditFormData] = useState({
        moviename: "",
        year: "",
        moviebudjet: "",
        category: "",
    });

    const handleEditFormChange = (event) => {
        event.preventDefault();

        const name = event.target.getAttribute("name");
        const value = event.target.value;

        const newFormData = { ...editFormData };
        newFormData[name] = value;

        setEditFormData(newFormData);
    };




    let editIntoApi = async (event) => {
        event.preventDefault();
        let moviename = document.getElementById('moviename').value;
        let movieyear = document.getElementById('year').value;
        let moviebudjet = document.getElementById('moviebudjet').value;
        let err = document.getElementById('error')





        if (moviename === null || moviename === "") {
            setErrorMessage("movie name is required")
            err.style.visibility = "visible"
        } else if (movieyear === null || movieyear === "") {
            setErrorMessage("movieyear is required")
            err.style.visibility = "visible"
        } else if (isNaN(movieyear)) {
            setErrorMessage("movieyear should be a number not text")
            err.style.visibility = "visible"
        } else if (moviebudjet === null || moviebudjet === "") {
            setErrorMessage("moviebudjet is required")
            err.style.visibility = "visible"
        } else if (isNaN(moviebudjet)) {
            setErrorMessage("moviebudjet should be a number not text")
        } else {        

            try {
                 axios.put('/movies', {
                     "title": editFormData.moviename,
                     "year": parseInt(editFormData.year),
                     "budget": parseInt(editFormData.moviebudjet),
                    //  "category_ids": parseInt([addFormData.category]),
                 }).then((response) => {
                    setErrorMessage('')
                    err.style.visibility = "hidden"
                    console.log(response.data)
                })

            } catch (error) {
                console.log(error)
            }
        }




    }


    const handledeletePatient = async (patient_id) => {
      try {
        await axios.delete(`/patient/patient/${patient_id}/`);
        getPatients();
      } catch (error) {
        console.log(error);
      }
    };





      return (
        <>
            <div className="container text-center" style={{ marginTop: "20px", padding: "50px" }}>
                <div className="row">
                    {movies.map((movie, index) => {

                        return (
                            <div className="col" key={index}>
                                <Card style={{ width: '18rem', marginTop: '20px' }}>
                                    <Card.Header>{movie.title}</Card.Header>
                                    <ListGroup variant="flush">
                                        <ListGroup.Item>{movie.year}</ListGroup.Item>
                                        <ListGroup.Item>{movie.budget}</ListGroup.Item>
                                        <ListGroup.Item>
                                            <button className="btn btn-danger">Edit</button>
                                            <button className="btn btn-success">Delete</button>
                                        </ListGroup.Item>
                                    </ListGroup>
                                </Card>

                            </div>
                        )
                    })
                    }
                </div>
            </div>
        </>
    );


}

export default MovieList2;
